class ToggleAdd{constructor(e){this.users=e,this.toggler=$(this.users)[0].lastElementChild.children,this.add(this.users)}add(e){$(this.toggler).click((function(s){s.preventDefault();$.ajax({type:"post",url:$(this).attr("href"),success:function(s){let i=s.data.otherUser;e.remove();new Noty({theme:"semanticui",text:"Friend added successfully",type:"success",timeout:400,layout:"topRight"}).show(),$("#myfriends").prepend($(`<div class="friends" id="user-${i._id}">\n                    <div id="friends-img-box">\n                        <div id="friends-img">\n                            <img src="${i.avatar}"  alt="">\n                            <div id="status"></div>\n                        </div>\n                        <p><a href="/home/profile/${i._id}">${i.first}  ${i.last}</a></p>\n                    </div>\n                    <div id="add-box">\n                        <a class="toggle-remove" style="background-color:rgb(185, 36, 36) ;" href="#">\n                            <span>Remove</span>\n                        </a>\n                    </div>\n                </div>\n                `))},error(e){console.log("err:::",e)}})}))}}